script:
frappe.ui.form.on('Sales Invoice', {
	refresh(frm) {
		// your code here
	}
})

frappe.ui.form.on('Sales Invoice Item', {
	// cdt is Child DocType name i.e Quotation Item
    // cdn is the row name for e.g bbfcb8da6a
    item_code(frm, cdt, cdn) {
        let item_row = frappe.get_doc(cdt, cdn); //get the new item row
        let item_code = item_row.item_code;

        if(item_code.toLowerCase().startsWith("plot ")){ //test if item is a plot
            let plot_id = item_code.substr(5).toString(); //strip off "Plot " from Plot Item title

            frm.set_value('update_stock', '1');
            if (frm.set_warehouse === "" || !frm.set_warehouse) {
                frappe.call({
                    method: "frappe.client.get",
                    args: {
                        doctype: "Land Settings"
                    },
                    callback(r) {
                        if(r.message) {          
                            frm.set_value('set_warehouse', r.message.sales_land_warehouse);
                        }
                    }
                });

            }


            //get Plot with matching plot name and assign its values to those of sales invoice item
            frappe.call({
    			"method": "frappe.client.get",
    			"args": {
    				"doctype": "Plot",
    				"name": plot_id
    			},
    			"callback": function(data) {
    				let plot = data.message;

    				if (plot) {
    					item_row.plot_id = plot_id;
                        item_row.land_project = plot.project;
                        item_row.subdivision = plot.subdivision;
    				}
    			}
    		});
        }
    }
});
